mapper.insert:
insert into article (id, digest, title, 
  reading, comments, likes, 
  tags_id, type_id, article
  )
values (#{id,jdbcType=BIGINT}, #{digest,jdbcType=VARCHAR}, #{title,jdbcType=VARCHAR}, 
  #{reading,jdbcType=BIGINT}, #{comments,jdbcType=BIGINT}, #{likes,jdbcType=BIGINT}, 
  #{tagsId,jdbcType=INTEGER}, #{typeId,jdbcType=INTEGER}, #{article,jdbcType=LONGVARCHAR}
  )

mapper.select:
select id, digest, title, reading, comments, likes, tags_id, type_id, article
from article
where id = #{id,jdbcType=BIGINT}

select id, digest, title, reading, comments, likes, tags_id, type_id, article
from article
where title=#{title}
